import { Button, Checkbox, Form, Input, Modal } from "antd";
import React from "react";
import { Todo } from "../types";
import { FormModal } from "./FormModal";

interface ModalHolderProps {
  isModalEditTodoOpen: boolean;
  setIsModalEditOpen: React.Dispatch<React.SetStateAction<boolean>>;
  setTodos: React.Dispatch<React.SetStateAction<Todo[]>>;
  todos: Todo[];
  titleEdit: string;
  setTitleEdit: React.Dispatch<React.SetStateAction<string>>;
  descriptionEdit: string;
  setDescriptionEdit: React.Dispatch<React.SetStateAction<string>>;
  statusEdit: boolean;
  setStatusEdit: React.Dispatch<React.SetStateAction<boolean>>;
  idEdit: string;
  setIdEdit: React.Dispatch<React.SetStateAction<string>>
}

const ModalEditTodo = (props: ModalHolderProps) => {

  const handleOk = () => {
    props.setIsModalEditOpen(false);
  };

  const handleCancel = () => {
    props.setIsModalEditOpen(false);
  };


  return (
    <>
      <Modal mask={false} title="Change the title, the description or the status" open={props.isModalEditTodoOpen} onOk={handleOk} onCancel={handleCancel}>
      <Form
          name="basic"
          labelCol={{ span: 8 }}
          wrapperCol={{ span: 16 }}
          style={{ maxWidth: 600 }}
          initialValues={{ remember: false }}
          autoComplete="off"
          preserve={false}
        >
          <Form.Item
            label="Title"
            name="title"
            >
            <Input onChange={onChangeTitle} placeholder=""/>
          </Form.Item>
          <Form.Item
            label="description"
            name="description"
          >
            <Input onChange={onChangeDescription} placeholder=""/>
          </Form.Item>
          <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
            <Button type="primary" htmlType="submit" onClick={handleSubmit}>
              Submit
            </Button>
          </Form.Item>
          <Checkbox onChange={onStatusChange}>Done</Checkbox>
        </Form>
      </Modal>
    </>
  )

}

export default ModalEditTodo
