import {useEffect, useState} from 'react';
import './App.css';
import { Todo } from './types';
import TodoList from './components/TodoList'
import axios from 'axios';
import Month from './components/Month';
import { daysInMonth } from './services/misc';

function App() {
  const [todos, setTodos] = useState<Todo[]>([])
  const [month, setMonth] = useState<number>(1)
  const [amountOfDays, setAmountofDays] = useState<number>(0)

  useEffect(() => {
    axios.get('http://localhost:3001/api/todos')
    .then(response => {
      const thisMonth = new Date().getMonth();
      const thisYear = new Date().getFullYear();
      setMonth(thisMonth + 1)
      setTodos(response.data.filter((aTodo: Todo) => aTodo.month == month))
      setAmountofDays(daysInMonth(thisMonth, thisYear))      
    })
  }, [])

  return (
    <div >
      <Month month={month} setMonth={setMonth} todos={todos} setTodos={setTodos} />
      <TodoList todos={todos.filter((aTodo: Todo) => aTodo.month == month)} setTodos={setTodos} month={month}/>
    </div>
  );
}

export default App;
