import React, { useState } from "react";
import { Todo } from "../types";
import axios from "axios";
import {Button, Checkbox, Form, Input} from 'antd'
import { DeleteOutlined } from "@ant-design/icons";
import type { CheckboxChangeEvent } from 'antd/es/checkbox';

interface TodoProps {
  todo: Todo;
  setTodos: React.Dispatch<React.SetStateAction<Todo[]>>;
  todos: Todo[];
  // month: number
}

export const FormModal= (props: TodoProps) => {
  const [title, setTitle] = useState<string>(props.todo.title)
  const [description, setDescription] = useState<string>(props.todo.description)
  const [status, setStatus] = useState<boolean>(props.todo.status)

  const onChangeTitle: React.ChangeEventHandler<HTMLInputElement> = (event) => {
    event.preventDefault();
    setTitle(event.target.value)
  }

  const onChangeDescription: React.ChangeEventHandler<HTMLInputElement> = (event) => {
    event.preventDefault();
    setDescription(event.target.value)
  }

  const onStatusChange = (event: CheckboxChangeEvent) => {
    event.preventDefault();
    setStatus(event.target.checked)
  }

  const handleSubmit = (event: { preventDefault: () => void;})=> {
    event.preventDefault();
    console.log(title, description, status);
    axios.put('http://localhost:3001/api/todos', {id: props.todo.id, month: props.todo.month, day: props.todo.day, title: title, description: description, status: status})
    .then(res => {
      console.log('from handle submit', res.data);
      props.setTodos(props.todos.map((aTodo: Todo) => aTodo.id !== props.todo.id ? aTodo : res.data))
    })
  }

  const handleDelete = (event: { preventDefault: () => void;}): void => {
    event.preventDefault();
    axios.delete(`http://localhost:3001/api/todos/${props.todo.id}`)
    .then(res => {
      console.log('from handle submit', res.data);
      props.setTodos(props.todos.filter((aTodo: Todo) => aTodo.id !== props.todo.id))
    })
  }

  return (
    <Form
    name="basic"
    labelCol={{ span: 8 }}
    wrapperCol={{ span: 16 }}
    style={{ maxWidth: 600 }}
    initialValues={{ remember: true }}
    autoComplete="off"
  >
    <Form.Item
      label="Title"
      name="title"
    >
      <Input placeholder={title} onChange={onChangeTitle}/>
    </Form.Item>

    <Form.Item
      label="description"
      name="description"
    >
      <Input placeholder={description} onChange={onChangeDescription}/>
    </Form.Item>

    <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
      <Button type="dashed" htmlType="submit" onClick={handleDelete}>
        Delete
        <DeleteOutlined />
      </Button>
    </Form.Item>

    <Checkbox onChange={onStatusChange}>Done</Checkbox>

    <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
      <Button type="primary" htmlType="submit" onClick={handleSubmit}>
        Submit
      </Button>
    </Form.Item>

  </Form>
  )

}
