import React, { useState } from "react";
import {  Button, Form, Input, Modal } from "antd";
import { PlusCircleOutlined } from "@ant-design/icons";
import { Todo } from "../types";
import { Dayjs } from "dayjs";
import axios from "axios";

interface ModalAddTodoInterface {
  isModalAddTodoOpen: boolean;
  setIsModalAddTodoOpen: React.Dispatch<React.SetStateAction<boolean>>;
  setTodos: React.Dispatch<React.SetStateAction<Todo[]>>;
  todos: Todo[];
  value: Dayjs
}


const ModalAddTodo = (props: ModalAddTodoInterface) => {

  const [title, setTitle] = useState<string>("")
  const [description, setDescription] = useState<string>("")
  const [status, setStatus] = useState<boolean>(false)

  const onChangeTitle: React.ChangeEventHandler<HTMLInputElement> = (event) => {
    event.preventDefault();
    setTitle(event.target.value)
  }

  const onChangeDescription: React.ChangeEventHandler<HTMLInputElement> = (event) => {
    event.preventDefault();
    setDescription(event.target.value)
  }

  const handleSubmit = (event: { preventDefault: () => void;})=> {
    event.preventDefault();
    console.log(title, description, status);
    console.log(props.value.date());

    axios.post('http://localhost:3001/api/todos', {month: props.value.month(), day: props.value.date(), title: title, description: description, status: status})
    .then(res => {
      console.log('from handle submit', res.data);
      props.setTodos(props.todos.concat(res.data))
    })
  }

  const handleOk = () => {
    props.setIsModalAddTodoOpen(false);
  };

  const handleCancel = () => {
    props.setIsModalAddTodoOpen(false);
  };

  const showModal = () => {
    props.setIsModalAddTodoOpen(true);
  }

  return (
    <>
      <PlusCircleOutlined onClick={showModal}/>
      <Modal className="modal" mask={false} title="Add a new todo" open={props.isModalAddTodoOpen} onOk={handleOk} onCancel={handleCancel}>
        <Form
          name="basic"
          labelCol={{ span: 8 }}
          wrapperCol={{ span: 16 }}
          style={{ maxWidth: 600 }}
          initialValues={{ remember: true }}
          autoComplete="off" 
        >
          <Form.Item
            label="Title"
            name="title"
            >
            <Input onChange={onChangeTitle}/>
          </Form.Item>
          <Form.Item
            label="description"
            name="description"
          >
            <Input onChange={onChangeDescription}/>
          </Form.Item>
          <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
            <Button type="primary" htmlType="submit" onClick={handleSubmit}>
              Submit
            </Button>
          </Form.Item>
        </Form>
      </Modal>
    </>
  )
}

export default ModalAddTodo
