import { Button, Checkbox, Form, Input, Modal } from "antd";
import { CheckboxChangeEvent } from "antd/es/checkbox";
import axios from "axios";
import React, { useState } from "react";
import { Todo } from "../types";

interface ModalHolderProps {
  isModalEditTodoOpen: boolean;
  todos: Todo[];
  editTitle: string;
  editDescription: string;
  editId: string;
  editMonth: number;
  editStatus: boolean;
  setIsModalEditOpen: React.Dispatch<React.SetStateAction<boolean>>;
  setTodos: React.Dispatch<React.SetStateAction<Todo[]>>;
}

const ModalEditTodo = (props: ModalHolderProps) => {
  const [todo, setTodo] = useState<Todo | null>(props.todo);

  const [title, setTitle] = useState<string | null>(null)
  const [description, setDescription] = useState<string | null>(null)
  const [status, setStatus] = useState<boolean | null>(null)

  if (todo == null) {
    console.log('test');
  }

  const handleOk = () => {
    props.setIsModalEditOpen(false);
  };

  const handleCancel = () => {
    props.setIsModalEditOpen(false);
  };

  const onChangeTitle: React.ChangeEventHandler<HTMLInputElement> = (event) => {
    event.preventDefault();
    setTitle(event.target.value)
  }

  const onChangeDescription: React.ChangeEventHandler<HTMLInputElement> = (event) => {
    event.preventDefault();
    setDescription(event.target.value)
  }

  const handleSubmit = (event: { preventDefault: () => void;})=> {
    event.preventDefault();
    console.log('from handle submit', props.todo);
    if (todo) {
      axios.put('http://localhost:3001/api/todos', {id: todo.id, month: todo.month, day: todo.day, title: title, description: description, status: status})
      .then(res => {
        console.log('from handle submit', res.data);
        props.setTodos(props.todos.map((aTodo: Todo) => aTodo.id !== todo.id ? aTodo : res.data))
      })
    }

  }

  const onStatusChange = (event: CheckboxChangeEvent) => {
    event.preventDefault();
    setStatus(event.target.checked)
  }

  if (todo) {
    return (
      <>
        <Modal mask={false} title="Change the title, the description or the status" open={props.isModalEditTodoOpen} onOk={handleOk} onCancel={handleCancel}>
          <Form
            name="basic"
            labelCol={{ span: 8 }}
            wrapperCol={{ span: 16 }}
            style={{ maxWidth: 600 }}
            initialValues={{ remember: false }}
            autoComplete="off"
            preserve={false}
          >
            <Form.Item
              label="Title"
              name="title"
              >
              <Input onChange={onChangeTitle} placeholder={todo.title}/>
            </Form.Item>
            <Form.Item
              label="description"
              name="description"
            >
              <Input onChange={onChangeDescription} placeholder={todo.description}/>
            </Form.Item>
            <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
              <Button type="primary" htmlType="submit" onClick={handleSubmit}>
                Submit
              </Button>
            </Form.Item>
            <Checkbox onChange={onStatusChange}>Done</Checkbox>
          </Form>
        </Modal>
      </>
    )
  }
  return (
    <div></div>
  )

}

export default ModalEditTodo
