import axios from 'axios'
import { Todo } from '../types'
const baseUrl = 'http://localhost:3001/api/todos'

const getAll = (): Todo[] => {
  let allTodos : Todo[] = []
  fetch(baseUrl)
  .then(response => response.json)
  .then((res) => {
    allTodos = res
  })
  return allTodos
}


export {getAll}
