import React, { useState } from "react";
import { Calendar, Popover, Space, Button } from "antd";
import type {Dayjs} from "dayjs";
import { Todo } from "../types";
import ModalHolder from "./ModalHolder";
import { PlusCircleOutlined } from "@ant-design/icons";
import ModalAddTodo from "./ModalAddTodo";

interface CalendarDisplayInterface {
  todos: Todo[];
  setTodos: React.Dispatch<React.SetStateAction<Todo[]>>;
}

const statusToType : Record<string, string> = {
  "true": "success",
  "false": "error",
}

const CalendarDisplay = (props: CalendarDisplayInterface) => {
  const [isModalOpen, setIsModalOpen] = useState<boolean>(false);
  const [isModalAddTodoOpen, setIsModalAddTodoOpen] = useState<boolean>(false);
  const [selectedDay, setSelectedDay] = useState<Dayjs>();


  const showModal = () => {
    setIsModalOpen(true);
  };

  const changeSelectedDay = (event: Dayjs) => {
    setSelectedDay(event)
  }

  const getData = (value: Dayjs) => {
    const day = value.date()
    const month = value.month()
    const year = value.year()

    const array = props.todos.filter((aTodo: Todo) => {
      if (aTodo.day === day && aTodo.month -1 == month) {
        return true
      } else {
        return false
      }
    })

    return array
  }

  const dateCellRender = (value: Dayjs) => {
    const data: Todo[] = getData(value);

    if (data.length > 0) {
      return (
        <div>
          <ul className="events">
            {data.map((todo) => (
              <li key={todo.id} >
                <Space wrap>
                  <Popover placement="topLeft" title={todo.title} content={todo.description}>
                    <Button onClick={showModal}>{todo.title}</Button>
                    <ModalHolder isModalOpen={isModalOpen} setIsModalOpen={setIsModalOpen} todo={todo} setTodos={props.setTodos} todos={props.todos}/>
                  </Popover>
                </Space>
              </li>
            ))}
          </ul>
          <ModalAddTodo isModalAddTodoOpen={isModalAddTodoOpen} setIsModalAddTodoOpen={setIsModalAddTodoOpen} setTodos={props.setTodos} todos={props.todos} value={value}/>
        </div>

      );
    } else {
      return (
        <ModalAddTodo isModalAddTodoOpen={isModalAddTodoOpen} setIsModalAddTodoOpen={setIsModalAddTodoOpen} setTodos={props.setTodos} todos={props.todos} value={value}/>
      )
    }

  };

  return (
    <Calendar dateCellRender={dateCellRender} onSelect={changeSelectedDay}/>
  )
}

export default CalendarDisplay
