import React, { InputHTMLAttributes, SetStateAction, useState } from "react";
import { State } from "../types";
import { Todo } from "../types";
import axios from "axios";


interface TodoProps {
  todo: Todo;
  visibility: boolean;
  // toggler: React.Dispatch<SetStateAction<boolean>>;
  // stateForm: boolean
  // month: number
}

export const Form = (props: TodoProps) => {
  const [title, setTitle] = useState<string>("")
  const [description, setDescription] = useState<string>("")
  const [status, setStatus] = useState<boolean>(false)

  const onChangeTitle: React.ChangeEventHandler<HTMLInputElement> = (event) => {
    event.preventDefault();
    setTitle(event.target.value)
  }

  const onChangeDescription: React.ChangeEventHandler<HTMLInputElement> = (event) => {
    event.preventDefault();
    setDescription(event.target.value)
  }

  const changeStatus: React.MouseEventHandler<HTMLDivElement> = (event) => {
    event.preventDefault();
    setStatus(!status)
  }

  const handleSubmit: React.FormEventHandler<HTMLFormElement>= (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    console.log(title, description, status);
    axios.put('http://localhost:3001/api/todos', {id: props.todo.id, month: props.todo.month, day: props.todo.day, title: title, description: description, status: status})
    // props.toggler(false)
  }

  if (props.visibility) {
    return (
      <div>
        <form onSubmit={handleSubmit}>
          <label htmlFor="title">Title</label>
          <br />
          <input type="text" id="title" value={title} placeholder={props.todo.title} onChange={onChangeTitle}/>
          <br/>
          <br/>

          <label htmlFor="description">Description</label>
          <br />
          <input type="text" id="description" value={description} placeholder={props.todo.description} onChange={onChangeDescription}/>
          <br/>
          <br/>
          <button type="submit">Change todo</button>
          <div onClick={changeStatus}>{status ? "mark as undone" : "mark as done"} </div>

        </form>
      </div>
    )
  } else {
    return (
      null
    )
  }

}
