import React from "react";
import { Modal } from "antd";
import { PlusCircleOutlined } from "@ant-design/icons";
import { Todo } from "../types";

interface ModalAddTodoInterface {
  isModalAddTodoOpen: boolean;
  setIsModalAddTodoOpen: React.Dispatch<React.SetStateAction<boolean>>;
  setTodos: React.Dispatch<React.SetStateAction<Todo[]>>;
  todos: Todo[];
}


const ModalAddTodo = (props: ModalAddTodoInterface) => {

  const handleOk = () => {
    props.setIsModalAddTodoOpen(false);
  };

  const handleCancel = () => {
    props.setIsModalAddTodoOpen(false);
  };

  const showModal = () => {
    props.setIsModalAddTodoOpen(true);
  }

  return (
    <div>
      <PlusCircleOutlined onClick={showModal}/>
      <Modal  title="Add a new todo" open={props.isModalAddTodoOpen} onOk={handleOk} onCancel={handleCancel}>

      </Modal>
    </div>
  )
}

export default ModalAddTodo
