import React, { InputHTMLAttributes, useState } from "react";
import { State } from "../types";
import { Todo } from "../types";


const defaultFormData = {
  title: "",
  description: "",
  status: false,
  id: ""
}

interface TodoProps {
  todo: Todo;
  visibility: boolean
  // month: number
}

export const Form = (props: TodoProps) => {
  const [formData, setFormData] = useState(defaultFormData)
  const {title, description, status, id} = formData

  const handleSubmit: React.FormEventHandler<HTMLFormElement>= (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    console.log(console.log(formData));
  }

  const onChange: React.ChangeEventHandler<HTMLInputElement> = (e) => {
    console.log('from onChange:', e.target.value);
  }

  if (props.visibility) {
    return (
      <div>
        <form onSubmit={handleSubmit}>
          <label htmlFor="title">Title</label>
          <br />
          <input type="text" id="title" value={title} placeholder={props.todo.title} onChange={onChange}/>
          <br/>
          <br/>

          <label htmlFor="description">Description</label>
          <br />
          <input type="text" id="description" value={description} placeholder={props.todo.description} onChange={onChange}/>
          <br/>
          <br/>
          <button type="submit">Add todo</button>
        </form>
      </div>
    )
  } else {
    return (
      null
    )
  }

}
